import Image from 'next/image'
import { useState } from 'react'

interface SafeImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
}

export function SafeImage({ src, alt, width, height, className }: SafeImageProps) {
  const [error, setError] = useState(false)

  if (error) {
    return (
      <div
        className={`bg-background-secondary flex items-center justify-center ${className}`}
        style={{ width, height }}
      >
        <span className="text-text-secondary">Image not available</span>
      </div>
    )
  }

  return (
    <Image
      src={src}
      alt={alt}
      width={width}
      height={height}
      className={className}
      onError={() => setError(true)}
      loading="lazy"
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  )
}
